mapper.select:
		select * from t_level_his_anchor 


			AND userId like '%${userId}%'


			AND anchorLevel = #{anchorLevel}


			AND resultTime >= #{ltResultTime} 


			AND resultTime <= #{gtResultTime} 


			AND remark like '%${remark}%'


			${appendWhere}


        ORDER BY 


				${orderBy}


				id




				${descOrAsc}


				asc

mapper.update:
UPDATE t_level_his_anchor
userId = #{userId,jdbcType=VARCHAR},
anchorLevel = #{anchorLevel,jdbcType=VARCHAR},
resultTime = #{resultTime,jdbcType=VARCHAR},
remark = #{remark,jdbcType=VARCHAR}
and id = #{id,jdbcType=VARCHAR}
