mapper.select:
select t1.userid as userId,userlevelStr as userLevelStr,resulttime as resulTtime,reachorder as reachorder,icon as image,nickName from t_level_his_user t1 inner join t_user_info t2 on t1.userid = t2.userid
where t1.userid = #{userId}
where t1.reachorder = #{reachorder}
order by userlevel desc

select t1.userid as userId,userlevelStr as userLevelStr,resulttime as resulTtime,reachorder as reachorder from t_level_his_user t1
where t1.reachorder = #{reachorder}
order by userlevel desc

mapper.update:
UPDATE t_level_his_user


			userId = #{userId,jdbcType=VARCHAR},  


			  userLevel = #{userLevel,jdbcType=VARCHAR},  


			resultTime = #{resultTime,jdbcType=VARCHAR},  


			remark = #{remark,jdbcType=VARCHAR}  



	  and id = #{id,jdbcType=VARCHAR}
